sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	// Will store all the data
	var answer = [];

	//QueryParams outside of the relative path
	// city, country, make, model, yeark are the query params available
	var make = request.queryParams.make;
	var city =  request.queryParams.city;
	var country = request.queryParams.country;
	var model = request.queryParams.model;
	var year = request.queryParams.year;

	var tableName = 'x_1834695_vehicles_vehicles';
	var vehicleGr = new GlideRecord(tableName);
	if(make){
		vehicleGr.addQuery('make',make);
	}
	if(city){
		vehicleGr.addQuery('city',city);
	}
	if(country){
		vehicleGr.addQuery('country',country);
	}
	if(model){
		vehicleGr.addQuery('model',model);
	}
	if(year){
		vehicleGr.addQuery('city',city);
	}
	vehicleGr.query();


	if(!vehicleGr.hasNext()){
		response.setError(new sn_ws_err.NotFoundError('No data found inside the table..'));
		response.setStatus(404);
	}

	while(vehicleGr.next()){
		var vehicleObj = {
			"vin": vehicleGr.getValue('vin'),
			"make": vehicleGr.getValue('make'),
			"model": vehicleGr.getValue('model'),
			"year" : vehicleGr.getValue('year'),
			"city" : vehicleGr.getValue('city'),
			"country" : vehicleGr.getValue('country')
		};
		answer.push(vehicleObj);
	}

	response.setBody(answer);

})(request, response);
