sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	// get the vin to delete
	var vin = request.pathParams.vin;

	var answer = [];

	if(!vin){
		var serviceError = new sn_nw_err.ServiceError();
		serviceError.setStatus(400);
		serviceError.setMessage("Error: VIN paramater as undefined");
		serviceError.setDetail("The request doesn't contain a VIN");
		response.setError(serviceError);
	}

	var table = 'x_1834695_vehicles_vehicles';
	var vehicleDataGr = new GlideRecord(table);
	vehicleDataGr.addQuery('vin',vin);
	vehicleDataGr.query();

	//add a property that instead of deleting just sets them as unactive
	// in the mean time, delete demo records :>
	if(!vehicleDataGr.hasNext()){
		gs.info('Delete | No record found case');
		var serviceErrorv2 = new sn_ws_err.ServiceError();
		serviceErrorv2.setStatus(404);
		serviceErrorv2.setMessage("Error: Vin was not found");
		serviceErrorv2.setDetail("The Vin ["+vin+"] was not found inside the Vehicles Tables...");
		response.setError(serviceErrorv2);

	}else if (vehicleDataGr.next()){
		gs.info("The data with id: "+ vehicleDataGr.vin + ", will be deleted, " + " | Say bye to :" + vehicleDataGr.model);
		response.setStatus(200);
		// delete the thing here
		// vin, make, model, year,country , city
		var deletedVehicle = {
			"vin":vehicleDataGr.vin,
			"make":vehicleDataGr.make,
			"model":vehicleDataGr.model,
			"year":vehicleDataGr.year,
			"country":vehicleDataGr.country,
			"city":vehicleDataGr.city,
		};

		//deletes the record, goodby
		vehicleDataGr.deleteRecord();
		answer.push({"message":"This Vehicle was deleted..."})
		answer.push(deletedVehicle);
	}

	response.setBody(answer);

})(request, response);
